    //1- create array of only same order li raho f URL
    const currentItem = props.orders.filter((item)=>{
        return item.name === itemName
    })
    console.log('cuuuuuuuuuuuuuuuurent item');
    console.log(currentItem);
    const itemQuantity = currentItem.length

    //#########################

    function handlCartData(newOrderName) {
        props.cartChange(newOrderName)
    }

    return (
        <>
            <Details quantity= {itemQuantity} item = {item} cartChange={handlCartData} orders={props.orders}/>
            {/* <h1>orders :  {order} </h1> */}
            <h3 className='grid-title'>Might also like</h3>
            <ProductsGrid limit={3} items={props.items} />

        </>
    )
}
