import React from 'react';
import HomePage from './pages/HomePage';
import ProductPage from './pages/ProductPage';
import Header from './components/Header';
import Footer from './components/Footer';
import Newsletter from './components/Newsletter';
import CartPage from './pages/CartPage';
// importing data
import items from './data/store-items copy';

import {BrowserRouter, Route, Routes} from "react-router-dom";

class App extends React.Component {
  state = {
    data: items,
    orders:[]
  }
// {orders:dataFromChild}
  handlCart=(dataFromChild)=>{
    let newOrders = this.state.data.filter((item)=>{
      return item.name !== dataFromChild
    })
    this.setState({orders:newOrders})
    // this.setState(()=>{
    //   return this.state.data.map((item)=>{
    //     return item.name === dataFromChild
    //   })
    // }

    //   )
    console.log(newOrders);
    console.log(this.state.data[0].name);
  }
  render() {
    return (
      <BrowserRouter>
        <div className="container">
          <Header cart={this.state.orders.length}/>
          <Routes>
            <Route path="/" element={<HomePage items = {this.state.data}/>}/>
            <Route path="/product/:id" element={<ProductPage items = {this.state.data} cartChange={this.handlCart}/>}/>
            <Route path="/cart" element={<CartPage items = {this.state.data} />}/>
          </Routes>
          <Newsletter/>
          <Footer/>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
