import React from 'react'
import CartItem from './CartItem'
import CartTotal from './CartTotal'

export default function Cart(props) {
    function deleteOrder(dataFromChild) {
        console.log(props.orders);
        console.log("*******");
        // 1- create an array that hold all the repeated orders
        const dupOrders = props.orders.filter((order)=>{
            return order.name === dataFromChild
        })
        // 2- crate an array that hold all no duplicated orders
        const nonDupOrders = props.orders.filter((order)=>{
            return order.name !== dataFromChild
        })
        // const deletedOrder = dupOrders.shift()
        // delete one order from the duplicated orders
        // dupOrders.pop()
        console.log(dupOrders);
        console.log(nonDupOrders);
        dupOrders.pop()
        console.log("//////////////////////////");
        console.log(dupOrders);
        console.log(nonDupOrders);
        //3- create new array that hold the new order list
        const newOrderList = [...nonDupOrders, ...dupOrders]
        console.log(newOrderList);

    }
    return (
        <div className='cart'>
            <span className='cart__title'>Your Cart</span>

            {props.orders? props.orders.map((order,index)=>{
                return <CartItem key={index} id={index} name={order.name} price={order.price} image={order.image} quantity={5} onDelete={deleteOrder}  />
            }):null}

            <CartTotal orders={props.orders} quantity={props.orders ? props.orders.length:0}/>

        </div>
    )
}
