function App() {
  const [data] = useState(items)
  const [orders, setOrders] = useState([])
  // 1- function theat update the orders according to the new added orders
  function handleCart(dataFromChild){
    // get the item that have the same name and add it to the cart
    // use the name of the item to filter it 
    let newOrder = data.filter((item)=>{
