import React, { useState } from 'react'
import Details from '../components/Details'
import ProductsGrid from "../components/ProductsGrid"
import { useParams } from 'react-router-dom'

export default function ProductPage(props) {
    const itemName = useParams().id
    const item = props.items.filter((item)=>{
        return item.name === itemName
    })[0];
    //#########################
    // cart order stats
    let [order,setOrder] = useState([])
    function handlCartData(dataFromChild) {
        setOrder(dataFromChild)
        // props.cartChange(order)
    }

    return (
        <>
            <Details item = {item} cartChange={handlCartData}/>
            <h1>orders :  {order} </h1>
            <ProductsGrid limit={3} items={props.items} />

        </>
    )
}
