import React, {useState} from 'react'
import Button from './Button'

export default function Details(props) {
    const [active, setActive] = useState(false)
    const [pink,setPink] = useState("")
    const [blue,setBlue] = useState("")
    const [black,setBlack] = useState("")
    const [color,setColor] = useState("Pink")
    const [activeColors, setActColors] = useState({
        aPink:"",
        aBlue:"",
        aBlack:""
    })
    function handleClick(e) {
        // console.log(e.target.className.includes('active'));
        console.log(e.target.name)
        const {name} = e.target
        setActive(!active)
        setColor(e.target.name)
        if (name === "pink") {
            setActColors({
                aPink:"active",
                aBlue:"",
                aBlack:""
            })
            // setPink('active')
            // setBlue("")
            // setBlack("")

        } else if(e.target.name ==="blue"){
            setBlue('active')
            setBlack("")
            setPink("")
        }else{
            setBlack('active')
            setPink("")
            setBlue("")
        }
        // color? {color}: "Pink"
    }
    return (
        <div className='details'>
            <div className='details__images'>
                <div className='img-container'>
                    <img className='det-sml' src={props.item.image} alt={props.item.name} />
                    <img className='det-sml lft' src={props.item.image} alt={props.item.name} />
                    <img className='det-sml' src={props.item.image} alt={props.item.name} />
                </div>
            </div>
            <div className='details__main-img'>
                <img className='det-big' src={props.item.image} alt={props.item.name}/>
            </div>
            <div className='details__text'>
                <h2 className='details__price'>{props.item.price}</h2>
                <h2 className='details__name'>{props.item.name}</h2>
                <p className='details__description'>{props.item.description}</p>
                <div className='details__colors'>
                    <span className='color-choice'><span>Color</span> : {color}</span>
                    <div className='colors'>
                        <button name="pink" className={`color ${activeColors.aPink}`} onClick={handleClick}></button>
                        <button name="blue" className={`color ${activeColors.aBlue}`} onClick={handleClick}></button>
                        <button name="black" className={`color ${activeColors.aBlack}`} onClick={handleClick}></button>
                    </div>
                </div>
                <div className='details__cart'>
                    <span >01</span>
                    <Button content= "add to cart" type="btn-primary"/>
                </div>
            </div>
        </div>
    )
}
